beforeSaveCustomScript:
//formate link
if (posterLink && string\contains(posterLink, '/d/')) {
    $linkBeforeId = 'https://drive.google.com/thumbnail?id=';
    $linkAfterId = '&sz=w600';

    $linkParts = string\split(posterLink, '/');
    $fileId = array\at($linkParts, 5);

    posterLink = string\concatenate($linkBeforeId, $fileId, $linkAfterId);
}
