beforeSaveApiScript:
$maxBookedTickets = 3;

//before insert
if (entity\isNew()) {
    //check if ticket already exist
    if (record\exists('Ticket', 
            'row=', row, 
            'seat=', seat, 
            'status!=', 'notPaid',
            'eventId=', eventId)) {
        recordService\throwDuplicateConflict('Це місце вже зайняли');
    }


    //check number of tickets for this contact
    $ticketsCount = record\count('Ticket', 'phone=', phone, 'status=', "booked", 'eventId=', eventId);
    if ($ticketsCount >= $maxBookedTickets) {
        recordService\throwForbidden(string\concatenate('Бронювати можна не більше ', $maxBookedTickets, ' квитків'));
    }

    //get event map
    $map = record\attribute('Event', eventId, 'map');
    if (!$map) {
        recordService\throwBadRequest('Мапа не налаштована');
    }

    //remove skipers and spaces
    $map = string\replace($map, ' ', '');  //remove spaces
    $map = string\replace($map, '=\n', '');//remove row skiper
    $map = string\replace($map, '_', '');  //remove seat skiper

    //get template from map
    $mapAsArr = string\split($map, '\n');
    $mapRow = array\at($mapAsArr, row - 1);
    $mapSeat = string\substring($mapRow, seat - 1, 1);

    if (!$mapSeat) {
        recordService\throwBadRequest('Місце не знайдено');
    }

    //get ticketType
    $ticketTypeId = record\findOne('TicketType', 'createdAt', 'desc', 'eventId=', eventId, 'nameOnMap=', $mapSeat);
    if (!$ticketTypeId) {
        recordService\throwBadRequest('Шаблон не знайдено');
    }

    //set full place name
    $indexer = record\attribute('Event', eventId, 'indexer');
    $i = 0;
    $index = string\concatenate(row,  ' ', seat);
    while($i < array\length($indexer),
        (
            $indexerVal = array\at($indexer, $i);
            $indexPart = array\at(string\split($indexerVal, '-'), 0);

            if ($index == $indexPart) {
                $placeNameArr = array\at(string\split($indexerVal, '-'), 1);
                $placeName = array\at(string\split($placeNameArr, ','), 0);
                $placeNumber = array\at(string\split($placeNameArr, ','), 1);

                place = string\concatenate($placeName, ', Місце', $placeNumber);
            }
            $i = $i + 1;
        )
    );

    if (!place) {
        place = string\concatenate('Ряд: ', row, ' Місце: ', seat);
    }

    $phone = string\concatenate('+', phone);
    $clientId = record\findOne('Contact', 'createdAt', 'desc', 'phoneNumber=', $phone, 'phoneNumber!=', null);
    if (!$clientId) {
        $clientId = record\create('Contact',
            'firstName', name,
            'lastName', 'квиток',
            'emailAddress', email,
            'phoneNumber', $phone,
            'assignedUserId', env\userAttribute('id'),
            'teamsIds' = env\userAttribute('teamsIds')
        );
    }

    //ticket fields
    //eventId = $eventId;
    name = place;
    bookedStart = datetime\now();
    bookedEnd = datetime\addHours(bookedStart, record\attribute('Event', eventId, 'bookingTime'));
    contactId = $clientId;
    ticketTypeId = $ticketTypeId;
    price = record\attribute('TicketType', $ticketTypeId, 'price');
    assignedUserId = env\userAttribute('id');
    teamsIds = env\userAttribute('teamsIds');
}

//before update
if (!entity\isNew()) {
    if (entity\isAttributeChanged('screenshotId')) {
        $relatedTickets = record\findMany('Ticket', 10, 'createdAt', 'desc', 'phone=', phone, 'status=', 'booked', 'id!=', id);
        $i = 0;
        while($i < array\length($relatedTickets),
            (
                $relatedTicketId = array\at($relatedTickets, $i);
                record\update('Ticket', $relatedTicketId, 'screenshotId', screenshotId);
                $i = $i + 1;
            )
        );
    }
}

